mapper.insert:
insert into tb_charge_order (id, charge_money, reward_money, order_state, create_time, finish_time, customer_id, brand_id, shop_detail_id,
charge_balance, reward_balance, total_balance, number_day_now, arrival_amount, end_amount, type, charge_setting_id
)
values (#{id,jdbcType=VARCHAR}, #{chargeMoney,jdbcType=DECIMAL}, #{rewardMoney,jdbcType=DECIMAL},
  #{orderState,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}, #{finishTime,jdbcType=TIMESTAMP},
  #{customerId,jdbcType=VARCHAR}, #{brandId,jdbcType=VARCHAR}, #{shopDetailId,jdbcType=VARCHAR},
  #{chargeBalance,jdbcType=DECIMAL}, #{rewardBalance,jdbcType=DECIMAL}, #{totalBalance,jdbcType=DECIMAL},
  #{numberDayNow}, #{arrivalAmount}, #{endAmount}, #{type}, #{chargeSettingId}
  )

mapper.select:
SELECT o.id, o.charge_money, o.reward_money, o.finish_time, o.shop_detail_id, o.customer_id, IF (o.type IS NULL, 1, o.type) type,
IF ( c.id IS NULL, '没有填写', c.id ) cid,
IF ( c.operation_phone IS NULL, '没有填写', c.operation_phone ) operation_phone,
IF ( o.type = 0,if(c.customer_phone IS NULL,'没有填写',c.customer_phone),(select telephone from tb_customer r where r.id = o.customer_id) ) customer_phone FROM tb_charge_order o LEFT JOIN tb_charge_log c ON c.charge_order_id = o.id WHERE o.shop_detail_id=#{shopDetailId} AND o.order_state = 1 AND o.finish_time is not null
AND o.finish_time>=#{beginDate}
AND o.finish_time <= #{endDate }

SELECT id, create_time, order_state, charge_money, shop_detail_id, brand_id FROM tb_charge_order WHERE shop_detail_id = #{shopId} and
create_time>#{beginDate} and create_time<#{endDate}
AND order_state = 1

SELECT id, order_state, charge_money, shop_detail_id, brand_id FROM tb_charge_order WHERE brand_id = #{brandId} and
create_time>#{beginDate} and create_time<#{endDate}
AND order_state = 1

select id, charge_money, reward_money, charge_balance, reward_balance, type, finish_time from tb_charge_order where order_state = 1
and shop_detail_id = #{shopId}
and finish_time >= CONCAT(#{beginDate},' 00:00:00') and finish_time <= CONCAT(#{endDate},' 23:59:59')
