mapper.select:
SELECT * from tb_order where shop_detail_id = #{shopId} and
customer_id = #{customerId}
and ( (order_state != 1 and parent_order_id is null and order_mode != 5)
or (order_state = 1 and order_mode != 5 and parent_order_id is null)
or (order_mode = 5 and parent_order_id is null))

    and ORDER_STATE in

        #{state}



ORDER BY create_time DESC LIMIT
#{start},#{datalength}

SELECT
id,order_state,production_status,allow_continue_order,allow_cancel,allow_appraise
from tb_order where id = #{orderId}

SELECT * from tb_order

    where customer_id=#{customerId} and shop_detail_id=#{shopId} and
    create_time > #{beginDate}
    and order_state in

        #{state}

    ORDER BY create_time DESC LIMIT 0,1


    where id=#{orderId}

select * from tb_order where shop_detail_id=#{shopId} and create_time>#{date} and production_status in
#{state}
and closed=0

select push_order_time, id, ver_code, article_count, table_number, parent_order_id, order_money, order_mode, amount_with_children, (select sum(t.article_count) from tb_order as t where t.id = tb_order.id or parent_order_id = tb_order.id) as totalCount, (select count(1) from tb_order t2 where t2.parent_order_id = tb_order.id ) as childCount FROM tb_order WHERE shop_detail_id=#{shopId} AND DATE_FORMAT(NOW(),'%d %b %y') = DATE_FORMAT(create_time,'%d %b %y') and order_state in
#{orderState}
and production_status in
#{productionStatus}
and parent_order_id is null order by create_time DESC;

SELECT SUM(order_money) FROM tb_order WHERE id=#{orderId} OR parent_order_id=#{orderId}
and order_state not in (1,9)
and order_state != 9

SELECT
SUM(order_money) FROM tb_order WHERE id=#{orderId} OR parent_order_id=#{orderId}
and order_state != 9

select sum(article_count) from tb_order WHERE id=#{id} OR parent_order_id=#{id}
and order_state not in (1,9)
and order_state != 9

select * from tb_order
where parent_order_id=#{0}
and order_state != 9

select id from
tb_order where parent_order_id=#{0}
and order_state in (1,2,10,11,12)

select id from
tb_order where parent_order_id=#{0}
and order_state in (2,10,11,12)

SELECT SUM(ap.brandSellNum)brandSellNum,SUM(ap.salles)salles ,ap.articleFamilyId ,ap.articleFamilyName ,ap.peference ,ap.articleName from ( SELECT brand_report.articleId,brand_report.brandSellNum,IFNULL(brand_report.article_name,a.name) articleName,a.article_family_id articleFamilyId,f.name articleFamilyName ,brand_report.salles,f.peference
from ( SELECT left(article_id,32) articleId ,SUM(count)brandSellNum, SUM(final_price) salles
,article_name from tb_order_item where order_id in (SELECT id from tb_order where order_state in (2,10,11,12) and confirm_time >= #{beginDate} and confirm_time <= #{endDate} and brand_id=#{brandId} ) GROUP BY left(article_id,32) ) as brand_report LEFT JOIN tb_article a on a.id = brand_report.articleId LEFT JOIN tb_article_family f on a.article_family_id = f.id ) as ap where ap.articleFamilyId = #{articleFamilyId} ORDER BY ${sort}

mapper.update:
update tb_order
set production_status = 5
where id = #{orderId}

update tb_article


        current_working_stock = current_working_stock - #{count}


        current_working_stock = current_working_stock + #{count}


where id = #{articleId}

    and current_working_stock - #{count} >= 0

update tb_article_price


        current_working_stock = current_working_stock - #{count}


        current_working_stock = current_working_stock + #{count}


where id = #{articleId}

    and current_working_stock - #{count} >= 0

update tb_article
set is_empty = 0 , empty_remark = NULL
where id = #{articleId}

update tb_article_price
set empty_remark = NULL
where id = #{articleId}

mapper.select:
select op.id as orderPaymentItem_id,payment_mode_id from tb_order_payment_item op
LEFT JOIN tb_order o on o.id = op.order_id
where op.order_id = #{orderId}

select oi.id as orderItem_id,article_name,article_id from tb_order_item oi
LEFT JOIN tb_order o on o.id = oi.order_id
where oi.order_id = #{orderId}

SELECT tb_order.*,
(select sum(t.article_count) from tb_order as t where t.id = tb_order.id or parent_order_id = tb_order.id) as
totalCount
from tb_order where shop_detail_id = #{shopId}

    and ORDER_STATE in

        #{state}



    and production_status in

        #{state}


and DATE_FORMAT(NOW(),'%d %b %y') = DATE_FORMAT(create_time,'%d %b %y')
ORDER BY create_time ASC

select * from tb_order
WHERE
shop_detail_id=#{shopId}
AND
 create_time >#{dateBegin} AND create_time  <#{dateEnd}
and order_state = 1 and production_status != 5
and table_number is not null
order by create_time DESC;

select id from tb_order
WHERE
brand_id=#{brandId}
AND
 create_time >#{beginDate} AND create_time  <#{endDate}
AND order_state IN(2,10,11,12) AND  production_status NOT  in (2,3)

SELECT id,order_money from tb_order
WHERE
brand_id=#{brandId}
AND
 create_time >#{beginDate} AND create_time  <#{endDate}
and order_state in(2,10,11,12) and production_status in (2,3)

