mapper.select:
select sum(meal_all_number) from tb_order where shop_detail_id = #{shopId}
and   DATE_FORMAT(NOW(),'%d %b %y') = DATE_FORMAT(create_time,'%d %b %y')
and order_state in (2,10,11,12) and production_status in (2,3)

select sum(customer_count) from tb_order where shop_detail_id = #{shopId}
and   DATE_FORMAT(NOW(),'%d %b %y') = DATE_FORMAT(create_time,'%d %b %y')
and order_state in (2,10,11,12) and production_status in (2,3)

select sum(pay_value) from tb_order_payment_item
where order_id = #{orderId} and payment_mode_id = #{type}
and 0 > pay_value

select tod.serial_number,tod.order_state,tod.production_status,push_order_time, print_order_time,call_number_time,order_money,topi.id orderPaymentId,topi.pay_value,topi.payment_mode_id, toi.article_name,(toi.orgin_count - toi.refund_count) count from tb_order tod inner join tb_order_payment_item topi on tod.id = topi.order_id inner join tb_order_item toi on tod.id = toi.order_id
tod.serial_number = #{serialNumber}
tod.id = #{orderId}

SELECT o.id,o.shop_detail_id,o.create_time,o.order_state,o.order_money,o.production_status,c.id customer_id ,c.telephone, a.id appraise_id, a.level, a.content,oi.id orderPaymentItem_id , oi.payment_mode_id , oi.pay_value FROM tb_customer c INNER JOIN tb_order o ON c.id = o.customer_id LEFT JOIN tb_appraise a on a.order_id = o.id LEFT JOIN tb_order_payment_item oi ON o.id = oi.order_id WHERE c.id =#{customerId}
and o.create_time >= #{ beginDate }
and o.create_time <= #{ endDate }

select count(1) from tb_order
where shop_detail_id = #{shopId}
and table_number = #{tableNumber}
and pay_type = 1 and order_state = 1
and parent_order_id is null
and 6*60*60 >  now() - create_time
and customer_id != #{customerId}
