mapper.select:
  SELECT SUM(pay_value) payValue, payment_mode_id  FROM tb_order_payment_item
	WHERE order_id IN (
		SELECT id FROM tb_order o WHERE o.production_status in (2,3) AND o.shop_detail_id = #{shopId}
		AND  o.create_time>#{beginDate} AND o.create_time<#{endDate}
	)
	GROUP BY payment_mode_id

SELECT id,sum(pay_value)as order_money from tb_order_payment_item WHERE
pay_time>#{beginDate} and pay_time <#{endDate}
GROUP BY order_id
