mapper.update:
update tb_red_packet
red_money = #{redMoney,jdbcType=DECIMAL}, create_time = #{createTime,jdbcType=TIMESTAMP}, finish_time = #{finishTime,jdbcType=TIMESTAMP}, customer_id = #{customerId,jdbcType=VARCHAR}, brand_id = #{brandId,jdbcType=VARCHAR}, shop_detail_id = #{shopDetailId,jdbcType=VARCHAR}, red_remainder_money = #{redRemainderMoney,jdbcType=DECIMAL}, red_type = #{redType,jdbcType=INTEGER}, order_id = #{orderId}, state = #{state},
where id = #{id,jdbcType=VARCHAR}

update tb_red_packet set red_remainder_money = ${redRemainderMoney}

    ,finish_time = #{finishTime,jdbcType=TIMESTAMP}

where id = #{id}

mapper.select:
SELECT IFNULL(SUM(r.orderCount),0) useRedOrderCount, IFNULL(SUM(r.order_money), 0) useRedOrderMoney FROM ( SELECT IF(td.parent_order_id IS NULL,1,0) orderCount, td.order_money FROM tb_order td INNER JOIN tb_order_payment_item top ON td.id = top.order_id WHERE td.order_state IN (2, 10, 11, 12) AND td.production_status IN (2, 3) AND top.pay_time >= CONCAT(#{useBeginDate},' 00:00:00') AND top.pay_time <= CONCAT(#{useEndDate},' 23:59:59') AND top.to_pay_id in (
select tco.id from tb_charge_order tco where tco.order_state = 1 and tco.reward_money > 0 and tco.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and tco.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and tco.shop_detail_id = #{shopDetailId}
select trp.id from tb_red_packet trp where trp.red_type in (${redType}) and trp.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and trp.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and trp.shop_detail_id = #{shopDetailId}
select tgn.id from tb_get_number tgn where tgn.state in (3) and tgn.final_money > 0 and tgn.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and tgn.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and tgn.shop_detail_id = #{shopDetailId}
select id from tb_coupon where using_time >= CONCAT(#{useBeginDate},' 00:00:00') and using_time <= CONCAT(#{useEndDate},' 23:59:59') and is_used = 1 and coupon_type = ${couponTypeInt}
) GROUP BY td.id ) r
