mapper.update:
update tb_red_packet
red_money = #{redMoney,jdbcType=DECIMAL}, create_time = #{createTime,jdbcType=DATETIME}, finish_time = #{finishTime,jdbcType=DATETIME}, customer_id = #{customerId,jdbcType=VARCHAR}, brand_id = #{brandId,jdbcType=VARCHAR}, shop_detail_id = #{shopDetailId,jdbcType=VARCHAR}, red_remainder_money = #{redRemainderMoney,jdbcType=DECIMAL}, red_type = #{redType,jdbcType=INT},
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select CONCAT_WS(',',( IFNULL((select COUNT(r.count) from ( select COUNT(1) count from tb_order td inner join tb_order_payment_item top on td.id = top.order_id where td.order_state in (2,10,11,12) and td.production_status in (2,3) and top.payment_mode_id = 7 and top.pay_time >= CONCAT(#{useBeginDate},' 00:00:00') and top.pay_time <= CONCAT(#{useEndDate},' 23:59:59') and top.result_data in
#{redPacketId}
and td.parent_order_id is null GROUP BY td.id) r),0)),( IFNULL((select SUM(r.orderMoney) from ( select SUM(td.order_money) orderMoney from tb_order td inner join tb_order_payment_item top on td.id = top.order_id where td.order_state in (2,10,11,12) and td.production_status in (2,3) and top.payment_mode_id = 7 and top.pay_time >= CONCAT(#{useBeginDate},' 00:00:00') and top.pay_time <= CONCAT(#{useEndDate},' 23:59:59') and top.result_data in
#{redPacketId}
GROUP BY td.id) r),0))) useOrder;
