mapper.select:
select CONCAT_WS(',',( IFNULL((select COUNT(1) from ( select COUNT(1) count from tb_order td inner join tb_order_payment_item top on td.id = top.order_id where td.order_state in (2,10,11,12) and IF(td.is_pos_pay = 1,td.is_pos_pay = 1,td.production_status in (2,3)) and top.payment_mode_id in (${payMode}) and top.pay_time >= CONCAT(#{useBeginDate},' 00:00:00') and top.pay_time <= CONCAT(#{useEndDate},' 23:59:59') and top.result_data in (
select tco.id from tb_charge_order tco where tco.order_state = 1 and tco.reward_money > 0 and tco.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and tco.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and tco.shop_detail_id = #{shopDetailId}
select trp.id from tb_red_packet trp where trp.red_type in (${redType}) and trp.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and trp.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and trp.shop_detail_id = #{shopDetailId}
select tgn.id from tb_get_number tgn where tgn.state in (3) and tgn.final_money > 0 and tgn.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and tgn.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and tgn.shop_detail_id = #{shopDetailId}
select id from tb_coupon where using_time >= CONCAT(#{useBeginDate},' 00:00:00') and using_time <= CONCAT(#{useEndDate},' 23:59:59') and is_used = 1 and coupon_type = ${couponTypeInt}
) and td.parent_order_id is null GROUP BY td.id) r),0)),( IFNULL((select SUM(r.orderMoney) from ( select td.order_money orderMoney from tb_order td inner join tb_order_payment_item top on td.id = top.order_id where td.order_state in (2,10,11,12) and IF(td.is_pos_pay = 1,td.is_pos_pay = 1,td.production_status in (2,3)) and top.payment_mode_id in (${payMode}) and top.pay_time >= CONCAT(#{useBeginDate},' 00:00:00') and top.pay_time <= CONCAT(#{useEndDate},' 23:59:59') and top.result_data in (
select tco.id from tb_charge_order tco where tco.order_state = 1 and tco.reward_money > 0 and tco.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and tco.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and tco.shop_detail_id = #{shopDetailId}
select trp.id from tb_red_packet trp where trp.red_type in (${redType}) and trp.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and trp.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and trp.shop_detail_id = #{shopDetailId}
select tgn.id from tb_get_number tgn where tgn.state in (3) and tgn.final_money > 0 and tgn.create_time >= CONCAT(#{grantBeginDate},' 00:00:00') and tgn.create_time <= CONCAT(#{grantEndDate},' 23:59:59') and tgn.shop_detail_id = #{shopDetailId}
select id from tb_coupon where using_time >= CONCAT(#{useBeginDate},' 00:00:00') and using_time <= CONCAT(#{useEndDate},' 23:59:59') and is_used = 1 and coupon_type = ${couponTypeInt}
) GROUP BY td.id) r),0))) useOrder;
